document.styles:
:root {
  --color-primary: #005AF0;
  --color-text-light: #fff;
}
amp-story-page {
  font-family: 'Roboto', sans-serif;
  background: var(--color-text-light);
}
amp-story-grid-layer {
  align-items: center;
  justify-items: center;
}
.square,
.circle {
  background: var(--color-primary);
  width: 96px;
  height: 96px;
}
.circle {
  border-radius: 80px;
}
