document.styles:
:root {
  --space-2: 1rem;   /* 16px */
  --space-3: 1.5rem; /* 24px */
}
.lightbox {
  height: 100vh;
  width: 100vw;
  background: rgba(0, 0, 0, 0.7);
  padding: 5% 0;
}
.lightbox-content {
  margin: 0 auto;
  padding: var(--space-2);
  background: #fff;
  border-radius: 5px;
  max-width: 700px;
  width: 95%;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
}
.message {
  display: flex;
  flex-direction: column;
  align-items: center;
}
.message > h2,
.message > * {
  margin: var(--space-2) 0;
  min-width: 200px;
  text-align: center;
}
.dismiss-button {
  position: absolute;
  right: var(--space-3);
  top: var(--space-2);
  cursor: pointer;
}
#post-consent-ui {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: var(--space-3);
}
