document.styles:
body {
  margin: 0;
  background: white;
  font-family: sans-serif;
}

header {
  background: white;
  padding: 10px 20px;
  border-bottom: 1px solid #ddd;
  position: fixed;
  left: 0;
  right: 0;
  top: 0;
  z-index: 1;
  line-height: 40px;
  font-size: 18px;
}

main {
  margin: 0 auto;
  padding: 80px 0 0 0;
  width: 100vw;
}

.content {
  height: 200vh;
  width: calc(70vw - 20px);
  padding: 0 20px;
  box-sizing: border-box;
}

aside {
  right: 20px;
  margin-left: 70vw;
  position: fixed;
  top: 80px;

  /*
  Define a viewport-based width for the aside. The docking slot will expand
  to fill its width.
  */
  width: 30vw;
}

.more-content {
  padding: 20px 20px 0;
  background: #eaeaea;

  /*
  Offset vertical axis by the negative height of the docking slot.
  Since the docking slot is 30vw wide, its height is 16.85vw based on a 16:9
  aspect ratio
  */
  transform: translateY(-16.85vw);
}

.more-content ul {
  margin: 0;
  padding: 0;
  list-style: none;
}

.more-content li {
  display: block;
  margin: 0 -20px;
}

.more-content li a {
  display: block;
  border-top: 1px solid #ddd;
  text-decoration: none;
  padding: 16px 20px;
  color: #2ac;
}

h3 {
  margin: 0;
  padding-bottom: 20px;
}

/* This is only needed for the preview to work on AMP by Example. You don't
need to include these set of properties. */
#abe-preview > div {
  width: 100vw;
  max-width: 100vw;
}

/* Ditto. */
.abe-experiment-warning {
  max-width: calc(70vw - 60px);
}

@media (max-width: 600px) {
  /* Hide on smaller devices to fallback to standard docking. */
  #dock-slot {
    display: none;
  }

  /* Reset fixed items. */
  aside,
  header {
    position: static;
  }

  aside {
    margin-left: 0;
    width: 100%;
  }

  .content {
    width: 100vw;
  }

  main {
    padding-top: 20px;
  }
}
