document.styles:
/*
 * Fidget Spinner styles
 */
.fidget-spinner-scene {
  margin: 10px 20%;
}

/*
 * Card transition styles
 */
.card {
  margin: 10%;
  position: relative;
}
.card-title {
  padding: 5%;
  padding-right: 40%;
  font-size: 50%;
  font-weight: bold;
  color: #FAFAFA;
  transform: translateX(-100%);
}

/*
 * Parallax window styles
 */
.parallax-image-window {
  overflow: hidden;
}
.parallax-image-window amp-img {
  margin-bottom: -30%;
}

/*
 * Carousel transition styles
 */
.carousel-component {
  margin-right: -60%;
  margin-left: 60%;
}
.carousel-parent {
  overflow: hidden;
}
