document.styles:
/* Wrapper that hosts the video and the overlay */
.video-player {
  position: relative;
  overflow: hidden;
}

/* Overlay fills the parent and sits on top of the video */
.click-to-play-overlay {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
}

.poster-image {
  position: absolute;
  z-index: 1;
}

.poster-image img {
  object-fit: cover;
}

.video-title {
  position: absolute;
  z-index: 2;

  /* Align to the top left */
  top: 0;
  left: 0;

  font-size: 1.3em;
  background-color: rgba(0,0,0,0.8);
  color: #fafafa;
  padding: 0.5rem;
  margin: 0px;
}

.play-icon {
  position: absolute;
  z-index: 2;

  width: 100px;
  height: 100px;

  background-image: url(http://localhost:8080/static/samples/img/play-icon.png);
  background-repeat: no-repeat;
  background-size: 100% 100%;

  /* Align to the middle */
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);

  cursor: pointer;
  opacity: 0.9;
}

.play-icon:hover, .play-icon:focus {
  opacity: 1;
}
