document.styles:
:root {
  --space-2: 1rem;   /* 16px */
  --space-3: 1.5rem; /* 24px */
}
.consentPopup {
  padding: var(--space-3);
  margin: 0 auto;
  background: #fff;
  border-radius: 5px;
  position: relative;
  max-width: 700px;
  width: 95%;
}
.consentPopup > h2, /* overwrite ABE style */
.consentPopup > * {
  margin: 0;
  margin-bottom: var(--space-2);
}
.consentPopup > button {
  margin-right: var(--space-2);
}
.popupOverlay {
  height: 100vh;
  width: 100vw;
  background: rgba(0, 0, 0, 0.7);
  padding: 5% 0;
}
.dismiss-button {
  position: absolute;
  right: var(--space-3);
  top: var(--space-2);
  cursor: pointer;
}
#post-consent-ui {
  display: flex;
  justify-content: center;
  align-items: center;
  padding: var(--space-3);
}
