:root {
  --color-primary: #005AF0;
  --color-secondary: #00DCC0;

  --space-1: .5rem;  /* 8px */
  --space-2: 1rem;   /* 16px */
  --space-3: 1.5rem; /* 24px */
  --space-5: 4rem; /* 64px */

  --box-shadow-1: 0 1px 1px 0 rgba(0,0,0,.14), 0 1px 1px -1px rgba(0,0,0,.14), 0 1px 5px 0 rgba(0,0,0,.12);
}
.comments {
  margin: var(--space-2) 0;
  min-width: 320px;
  width: 100%;
}
.comments [overflow] {
  position: absolute;
  right: var(--space-3);
  top: var(--space-1);
}
.comment {
    background: var(--color-secondary);
    margin: var(--space-2);
    margin-top: 0;
    margin-left: var(--space-5);
    padding: var(--space-2);
    box-shadow: var(--box-shadow-1);
    position: relative;
}
.comment:before {
  content: ' ';
  height: 0;
  position: absolute;
  width: 0;
  left: 10px;
  border: 10px solid transparent;
  border-right-color: var(--color-secondary);
  top: 50%;
  margin-top: -28px;
  margin-left: -30px;
}
.comment:after {
  content: ' ';
  height: 48px;
  position: absolute;
  width: 48px;
  top: 0;
  left: -56px;
  background-repeat: no-repeat;
  background-image: url("data:image/svg+xml,%0A%3Csvg xmlns='http://www.w3.org/2000/svg' width='48' height='48' viewBox='0 0 24 24'%3E%3Cpath d='M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 3c1.66 0 3 1.34 3 3s-1.34 3-3 3-3-1.34-3-3 1.34-3 3-3zm0 14.2c-2.5 0-4.71-1.28-6-3.22.03-1.99 4-3.08 6-3.08 1.99 0 5.97 1.09 6 3.08-1.29 1.94-3.5 3.22-6 3.22z'/%3E%3Cpath d='M0 0h24v24H0z' fill='none'/%3E%3C/svg%3E");
}
.comment .user {
  color: var(--color-primary);
  font-weight: 700;
}
.comment .date {
  font-size: 12px;
}
.comment .placeholder {
  display: inline-block;
  height: 14px;
  width: 150px;
  background-color: #cccccc;
  position: relative;
  top: 3px;
}
.comment-form {
  margin: var(--space-2);
  margin-left: var(--space-5);
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: flex-start;
}
.comment-form > textarea {
  width: 100%;
  margin: var(--space-2) 0;
}
.comment-form > input {
  min-width: 320px;
}
.comment-sample {
  padding: var(--space-2);
  width: 100vw;
  max-width: 700px;
  min-height: 500px;
  box-sizing: border-box;
}
.comment-sample > button {
  margin: var(--space-2) 0;
}
