document.styles:
:root {
  --color-primary: #005AF0;
  --space-1: 0.5rem; /* 8px */
  --space-2: 1rem;   /* 16px */
  --space-3: 1.5rem; /* 24px */
}
.description {
  margin: 0;
  padding: 0;
}
.description li {
  list-style-type: square;
}
.housing-description {
  clear: both;
}
.image-gallery {
  margin: 0;
}
.additional-data p, .additional-data h2 {
  padding: var(--space-2);
}
.additional-data h2 {
  font-size: 18px;
}
.related {
  background-color: #f5f5f5;
  display: block;
  color: #111;
  height: 100px;
  padding: 0;
  padding-right: var(--space-1);
}
.related amp-img {
  line-height: 24px;
  font-weight: 400;
  font-size: 24px;
  vertical-align: middle;
  float: left;
  margin-right: 10px;
}
.related:active {
  background-color: #ccc;
}
.price-description {
  color: var(--color-primary);
  font-weight: 400;
}
.price-heading {
  margin-bottom: var(--space-2);
  margin-top: var(--space-3);
  padding: 0 var(--space-2);
}
.price-other {
  font-weight: bold;
}
.contact-section {
  margin-top: var(--space-2);
  display: flex;
  flex-wrap: wrap;
}
@media (max-width: 744px) {
  .contact-section {
    display: block;
  }
}
@media (max-width: 744px) {
  .contact {
    display: flex;
    margin-bottom: var(--space-2);
    margin-top: var(--space-3);
    justify-content: center;
    padding: 0 var(--space-2);
  }
}
.social-share {
  line-height: 36px;
  display: flex;
}
.list-overflow {
  position: absolute;
  bottom: 0;
}
.form-field-right {
  border: 1px solid #dfdfdf;
  background: white;
  width: 200px;
}
.form-field-left {
  width: 200px;
}
.ampstart-card {
  text-decoration: none;
}
