document.styles:
:root {
  --space-1: .5rem;  /* 8px */
  --space-2: 1rem;   /* 16px */
}
#carousel-with-preview amp-img {
  transition: opacity 1s ease-in-out;
}
.carousel-preview {
  display: flex;
  align-items: center;
  justify-content: center;
}
.carousel-preview > * {
  margin: var(--space-1);
  padding: 0;
}
.carousel-preview [selected] {
  opacity: 0.5;
  outline: none;
}
.carousel-preview .amp-carousel-slide {
  margin: var(--space-1);
  padding: 0;
}
.carousel-preview button:active {
  opacity: 0.5;
}
.carousel1, .carousel2 {
  background: var(--color-bg-light);
  margin: var(--space-2) 0;
}
.carousel1 .slide > amp-img > img{
  object-fit: contain;
}
.carousel2 .slide > amp-img > img{
  object-fit: contain;
}
.carousel2 .caption {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: var(--space-1);
  background: rgba(0, 0, 0, 0.6);
  font-size: smaller;
  max-height: 30%;
}

/* define some contants */
.collapsible-captions {
  --caption-height: 32px;
  --image-height: 300px;
  --caption-padding: 1rem;
  --button-size: 28px;
  --caption-color: #f5f5f5;;
  --caption-bg-color: #111;
  background: var(--caption-bg-color);
}
.collapsible-captions * {
  /* disable chrome touch highlight */
  -webkit-tap-highlight-color: rgba(255, 255, 255, 0);
  box-sizing: border-box;
}
/* see https://amp.dev/documentation/examples/style-layout/how_to_support_images_with_unknown_dimensions/ */
.collapsible-captions .fixed-height-container {
  position: relative;
  width: 100%;
  height: var(--image-height);
}
.collapsible-captions amp-img img {
  object-fit: contain;
}
.collapsible-captions figure {
  margin: 0;
  padding: 0;
}
/* single line caption */
.collapsible-captions figcaption {
  position: absolute;
  bottom: 0;
  margin: 0;
  width: 100%;
  /* inital height is one line */
  max-height: var(--caption-height);
  line-height: var(--caption-height);
  padding: 0 var(--button-size);
  /* cut text after first line and show an ellipsis */
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  /* animate expansion */
  transition: max-height 200ms cubic-bezier(0.4, 0, 0.2, 1);
  /* overlay the carousel icons */
  z-index: 1000;
  /* some styling */
  color: var(--caption-color);
  background: var(--caption-bg-color);
}
/* expanded caption */
.collapsible-captions figcaption.expanded {
  /* add padding and show all of the text */
  padding: var(--button-size);
  line-height: inherit;
  white-space: normal;
  text-overflow: auto;
  max-height: calc(var(--caption-height) + var(--image-height));
  /* show scrollbar in case caption is larger than image */
  overflow: auto;
}
/* don't show focus highlights in chrome */
.collapsible-captions figcaption:focus {
  outline: none;
  border: none;
}
/* the expand/collapse icon */
.collapsible-captions figcaption span {
  display: block;
  position: absolute;
  top: calc((var(--caption-height) - var(--button-size)) / 2);
  right: 2px;
  width: var(--button-size);
  height: var(--button-size);
  line-height: var(--button-size);
  text-align: center;
  font-size: 12px;
  color: inherit;
  cursor: pointer;
}
