document.styles:
:root {
--color-primary: #005AF0;
--color-secondary: #00DCC0;
--color-text-light: #fff;
--color-text-dark: #000;

--space-1: .5rem;  /* 8px */
--space-2: 1rem;   /* 16px */
}
.carousel .slide > amp-img > img {
  object-fit: contain;
}
.amp-social-share {
  margin-right: var(--space-1);
}
.heading {
  padding: var(--space-2);
}
.heading > #summary {
  font-weight: 500;
}
.heading > small {
  color: var(--color-primary);
}
.date {
  color: var(--color-primary);
  font-size: 12px;
}
.blog {
  padding: 0;
  min-width: 70%;
  background: var(--color-text-light);
}
.blog p {
  margin: 0 var(--space-2);
}
.blog .text {
  color: var(--color-text-dark);
  padding-top: 0;
}
.blog .title {
  color: var(--color-text-light);
  position: relative;
  top: -48px;
  margin: 0 var(--space-2);
  height: 0;
  padding-bottom: var(--space-2);
}
.blog .social-share {
  line-height:36px;
  display: flex;
  padding-bottom: var(--space-2);
  padding-top: var(--space-2);
}
#live-list-update-button {
  position: fixed;
  top: var(--space-1);
  right: var(--space-2);
}
@media (max-width: 600px) {
  #live-list-update-button {
    top: 60px;
  }
}
.pagination {
  display: inline-block;
  padding: 0;
}
.live-list [pagination] nav {
  display: flex;
  align-items: center;
  justify-content: center;
}
.pagination li {
  display: inline;
  padding: var(--space-1);
  list-style-type: none;
}
