document.styles:
:root {
  --color-primary: #005AF0;
  --color-secondary: #00DCC0;
  --color-text-light: #fff;
  --color-text-dark: #000;
  --color-error: #B00020;
  --color-bg-light: #FAFAFC;

  --space-1: .5rem;  /* 8px */
  --space-2: 1rem;   /* 16px */
  --space-3: 1.5rem; /* 24px */
  --space-4: 2rem;   /* 32px */

  --box-shadow-1: 0 1px 1px 0 rgba(0,0,0,.14), 0 1px 1px -1px rgba(0,0,0,.14), 0 1px 5px 0 rgba(0,0,0,.12);
}
.info {
  text-align: right;
  padding: 0 var(--space-1);
}
.items {
  display: flex;
  flex-flow: row wrap;
  justify-content: center;
}
.item {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  max-width: 304px;
  background: var(--color-bg-light);
  margin: var(--space-1);
  border-radius: 5px;
  overflow: hidden;
  box-shadow: var(--box-shadow-1);
}
@media(min-width: 480px) {
  .item {
    max-width: calc(50% - var(--space-1) - var(--space-1));
  }
}
.item > .title,
.item > .copy {
  margin: var(--space-1);
}
.image {
  max-width: 100%;
}
.item > .image > img {
  object-fit: cover;
  object-position: center;
}

[overflow] {
  height: 55px;
  background-image: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, .5));
}
[overflow] button {
  position: absolute;
  bottom: var(--space-1);
  left: var(--space-2);
  right: var(--space-2);
  width: calc(100% - 2 * var(--space-2));
  border: 0;
  padding: var(--space-1) var(--space-2);
  cursor: pointer;
}
.navigation {
  display: flex;
}
.prev,
.next {
  max-width: 50%;
  flex: 1 0 0;
  cursor: pointer;
}
.next {
  margin-left: auto;
}
.prev {
  margin-right: auto;
}
