document.styles:
  :root {
    --color-text-light: #fff;
    --box-shadow-1: 0 1px 1px 0 rgba(0,0,0,.14), 0 1px 1px -1px rgba(0,0,0,.14), 0 1px 5px 0 rgba(0,0,0,.12);
    --space-1: .5rem;  /* 8px */
    --space-2: 1rem; /* 16px */
    --space-3: 1.5rem; /* 24px */
  }
  .menu-wrap {
    display: flex;
    align-items: center;
  }
  .menu-wrap > * {
    margin: 0 10px;
  }
  article#preview {
    background: var(--color-text-light);
  }
 #hero-images {
   object-fit: contain;
 }
 #hero-images .caption {
   text-align: center;
   position: absolute;
   bottom: 0;
   left: 0;
   right: 0;
   padding: var(--space-3);
   background: rgba(200,200,200,.5);
   color: var(--color-text-light);
   font-size: 36px;
   max-height: 30%;
   font-weight: 300;
 }
 .list-overflow {
   width: 160px;
   margin-top: 150px;
 }
 .refine {
   display: none;
 }
 .filter-mobile {
   display: none;
 }

   /* global style in order to bind global style in the iframe*/

 html, body {
   font-size: 16px;
   font-family: 'Noto Sans', sans-serif;
   color: #48525C;
 }
 p {
   font-weight: 400;
   color: #48525c;
 }
 a {
   font-weight: bold;
   text-decoration: none;
   color: #005AF0;
 }
 label[for=sort] {
   margin: 20px;
 }
 .ampstart-btn {
   margin-bottom: 40px;
 }
 p.name + p.star {
   color: gold;
 }

 @media (min-width: 600px) {
   .refine {
     display:inline;
     float: left;
     width: 20%;
     box-shadow: var(--box-shadow-1);
     padding: var(--space-2);
   }
   .content {
     float: right;
     width: 80%;
   }
   .grid {
     display: flex;
     justify-content: space-around;
     flex-wrap: wrap;
   }
   .products {
     display: inline-block;
     margin: var(--space-2);
   }
 }
 @media (max-width: 500px) {
   .filter-mobile {
     display:inline;
   }
   .products {
     display: block;
     height: 100px;
     padding: 0 var(--space-1) 0 0;
   }
   .products amp-img {
     line-height: var(--space-3);
     font-weight: 400;
     font-size: var(--space-3);
     vertical-align: middle;
     float: left;
     margin-right: var(--space-1);
   }
 }
 #main-wrap {
   overflow: hidden;
 }
 #info-wrap {
   display: flex;
   justify-content: space-between;
   align-items: center;
   overflow: hidden;
   margin: var(--space-2)
 }
 .info {
   width: 50%;
   float: left;
 }
 amp-lightbox {
   background: var(--color-text-light);
   width: 100%;
   height: 100%;
   position: absolute;
 }
 #info-wrap .ampstart-input {
   width: 200px;
 }
 .autosuggest-container {
    position: relative;
  }

 .autosuggest-box {
    position: absolute;
    width: 100%;
    background-color: var(--color-text-light);
    box-shadow: var(--box-shadow-1);
  }
.recommendations, .bestsellers, .filter-section {
  margin: var(--space-2);
}
.filter-section > div:first-of-type {
  display: flex;
  justify-content: space-between;
  margin: 20px;
}
