document.styles:
amp-carousel amp-img > img {
  object-fit: contain;
}
amp-carousel figcaption {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 8px;
  background: rgba(0, 0, 0, 0.6);  /* translucent black */
  color: #ddd;
  font-size: smaller;
  max-height: 30%;
}

amp-carousel .review {
  width: 25em;
  vertical-align: top;
  overflow: auto;
  margin: 0;
  padding: 16px;
  height: 80%;  /* TODO easy way to ensure the bottom shadow is visible */
  white-space: normal;  /* the enclosing div generated by amp-carousel sets it to nowrap */
}

amp-carousel .review:nth-child(even) {background: #FFFFFF}
amp-carousel .review:nth-child(odd) {background: #94C2F9}

amp-carousel .review h1 {
  font-size: larger;
  padding: 0;
}
amp-carousel .review p {
  padding: 0;
}

amp-carousel .review address:before {
  content: '-- ';
}

ul, ol {
  padding: 0 16px;
}

/* Unicode-based stars and half-star credit: amoniker, https://coderwall.com/p/iml9ka/star-ratings-in-css-utf8 */
.star-icon {
  color: #ddd;
  font-size: 34px;
  position: relative;
}

.star-icon.full:before {
  color: #FDE16D;
  content: '\2605'; /* Full star in UTF8 */
  position: absolute;
  left: 0;
  text-shadow: 0 0 2px rgba(0,0,0,0.7);
}

.star-icon.half:before {
  color: #FDE16D;
  content: '\2605'; /* Full star in UTF8 */
  position: absolute;
  left: 0;
  width: 50%;
  overflow: hidden;
  text-shadow: 0 0 2px rgba(0,0,0,0.7);
}

.logo {
  background-position: left 16px center;
  background-repeat: no-repeat;
  background-image: -webkit-image-set(
      url(/static/samples/img/ic_menu_white_1x_web_24dp.png) 1x,
      url(/static/samples/img/ic_menu_white_2x_web_24dp.png) 2x
  );
  background-color: #333;
  text-align: left;
  padding: 16px;
  padding-left: 72px;
  box-shadow: 0 2px 2px 0 rgba(0,0,0,.14),0 3px 1px -2px rgba(0,0,0,.2),0 1px 5px 0 rgba(0,0,0,.12);
}
.logo > a {
  font-size: 16px;
  font-weight: 500;
  color: white;
  text-transform: uppercase;
}
.heading {
  padding-bottom: 8px;
}
.heading > #summary {
  font-weight: 500;
}
.heading > small {
  color: #656565;
}
.related {
  background-color: #f5f5f5;
  margin: 16px 16px;
  display: block;
  color: #111;
  height: 75px;
  padding: 0;
}
.related > span {
  font-size: 16px;
  line-height: 75px;
  font-weight: 400;
  vertical-align: top;
  margin: 8px;
}
.related:hover {
  background-color: #ccc;
}
