document.styles:
/* Main element that contains the creative. */
.root-container {
  background: #000;
  color: #fff;
  font-family: 'Roboto', sans-serif;
  font-size: 12px;
  overflow: hidden;
  width: 340px;
  height: 240px;
  position: relative;
  margin: 0 auto;
  display: block;
}
/* Position the footer absolutely, at the bottom of the container. */
.footer {
  background: #000;
  font-size: 12px;
  padding: 8px;
  text-transform: uppercase;
  display: flex;
  align-items: center;
  color: #fff;
  z-index: 2;
  position: absolute;
  left: 0;
  bottom: 0;
  right: 0;
}
.footer-logo {
  border-right: 1px solid #fff;
  padding: 0 8px 0 0;
  margin: 0 8px 0 0;
}
.logo-img {
  display: block;
}
.stretch {
  flex: 1;
}
.button {
  text-transform: uppercase;
  padding: 8px;
  color: #fff;
  display: inline-block;
  background-color: #2979ff;
  text-decoration: none;
}
.button:active {
  background-color: #92bbff;
}
/* Basic cards styling */
.cards-container {
  position: absolute;
  z-index: 1;
  left: 0;
  right: 0;
}
.card {
  width: 100%;
  overflow: hidden;
  position: relative;
}
/* Change position of the expand button for the middle card */
.card-gauges .expand-button {
  left: 90px;
  top: 50px;
}
.learn-more a:active {
  background-color: #f0f0f0;
}
