document.styles:
/* Main element that contains the creative. */
.root-container {
  font-family: 'Roboto', sans-serif;
  font-size: 12px;
  background: #151717 url(/static/samples/img/car-mesh.jpg) center center;
  background-size: cover;
  color: #fff;
  width: 100%;
  height: 100vh;
  position: relative;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}
.root-container h1 {
  font-weight: normal;
  margin: 10px 0 0;
  text-align: center;
  text-transform: uppercase;
  font-size: 12px;
}
.carousel-container {
  position: relative;
  flex: 1;
  display: flex;
  justify-content: center;
  flex-direction: column;
}
.carousel-img {
  display: block;
}
/*
 * Dimensions of the carousel item are the same as the size of each image
 * plus the border width. Using `calc()` for the sake of the example.
 */
.carousel-item {
  width: calc(216px + 2 * 4px);
  height: calc(117px + 2 * 4px);
  box-sizing: border-box;
  background: #000;
  float: left;
}
.carousel-item .carousel-img {
  border: 4px solid #c9cccc;
}
/* Styling for footer */
.footer {
  background: #000;
  box-sizing: border-box;
  padding: 8px;
  display: flex;
}
.stretch {
  flex: 1;
}
.logo-img {
  display: block;
}
.button {
  text-transform: uppercase;
  padding: 8px;
  color: #fff;
  display: inline-block;
  background-color: #2979ff;
}
.button-secondary {
  background: transparent;
  border-right: 1px solid white;
  margin-right: 8px;
}
