document.styles:
* {
  box-sizing: border-box;
}
amp-story * {
  font-family: 'Helvitica Nueve', sans-serif;
  color: white;
}
amp-story-page {
  background: white;
}
amp-story h1 {
  font-size: 46px;
}
amp-story h2 {
  font-size: 36px;
}
amp-story p {
  font-size: 16px;
  line-height: 24px;
}
.bold {
  font-weight: bold;
}
.bottom {
  align-content: end;
}
.medium {
  font-weight: 600;
}
.last {
  padding-bottom: 65px;
}
.center {
  text-align: center;
}
.icon {
  background-image: url(/img/AMP-Brand-White-Icon.svg);
  background-repeat: no-repeat;
  background-size: 50px 50px;
  height: 50px;
  object-fit: contain;
  width: 50px;
}
.byline {
  letter-spacing: 1.28px;
  padding-bottom: 58px;
}
