document.styles:
:root {
  --color-text-light: #fff;
  --space-2: 1rem;  /* 16px */
}
.fixed-dashboard {
    background-color: #e0e0e0;
    position: fixed;
    bottom: 0;
    right: 0;
    width: 100%;
    z-index: 100;
    margin: var(--space-2);
    box-shadow:0 1px 1px 0 rgba(0,0,0,.14),0 1px 1px -1px rgba(0,0,0,.14),0 1px 5px 0 rgba(0,0,0,.12);
}
amp-carousel .slide {
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 20px;
  background: green;
  color: var(--color-text-light);
}
