document.styles:
:root {
  --space-1: .5rem;  /* 8px */
}
#my-app-banner {
  position: fixed;
  bottom: 0;
  display: flex;
  width: 100%;
  justify-content: center;
  align-items: center;
  padding: var(--space-1);
}
#my-app-banner .banner-text {
  flex: 1;
  margin: 0 var(--space-1);
}
#my-app-banner button {
  border: none;
  background: none;
  -webkit-appearance: none;
}
