document.styles:
:root {
  --color-primary: #005AF0;
  --color-text-light: #fff;
  --space-1: .5rem;  /* 8px */
}
.square-small, .square-medium, .square-large {
  display: inline-block;
  margin: var(--space-1);
  color: var(--color-text-light);
  background-color: var(--color-primary);
}
.square-small {
  height: 100px;
  width:  100px;
}
.square-medium {
  height: 200px;
  width:  200px;
}
.square-large {
  height: 300px;
  width:  300px;
}
