document.styles:
:root {
  --space-1: .5rem;  /* 8px */
  --space-2: 1rem;   /* 16px */
  --space-3: 1.5rem; /* 24px */
}

/* Hides all inputs after successful form submission */
form.amp-form-submit-success.sample-form.hide-inputs > input {
  display: none;
}

/* sample styles */
.sample-form {
  padding: 0 var(--space-2);
}
.sample-form > * {
  margin: var(--space-1);
}
.sample-heading {
  margin: 0 var(--space-3);
  margin-top: var(--space-3);
  font-size: 18px;
}
