document.styles:
:root {
  --color-primary: #005AF0;
  --color-text-light: #fff;
  --space-1: .5rem;  /* 8px */
  --space-2: 1rem;   /* 16px */
}
.overlay-text {
  position: absolute;
  bottom: var(--space-2);
  left: var(--space-2);
  z-index: 1;
  pointer-events: none;
  background-color: var(--color-primary);
  color: var(--color-text-light);
  padding: var(--space-1);
  border-radius: 2px;
  opacity: 0.7;
}
.sample-figure {
  margin: 0;
}
.sample-figure figcaption {
  padding: var(--space-1) var(--space-2);
}
.paragraph {
  padding: var(--space-2);
}
