document.styles:
:root {
  --space-1: 0.5rem;
  --space-2: 1rem;   /* 16px */
  --space-4: 2rem;
}
.sample-sidebar {
  width: 250px;
  padding: var(--space-2);
}
.sample-sidebar > * {
  margin-top: var(--space-2);
}
.sample-sidebar li,
nav[toolbar] li {
  margin: var(--space-2);
  margin-left: var(--space-4);
  list-style: none;
}
amp-sidebar ul, nav[toolbar] ul {
  display: block;
  padding-left: 0;
}
.previewOnly {
  margin: var(--space-2);
}
#sidebar-right nav.amp-sidebar-toolbar-target-shown {
    display: none;
}
button {
  margin-left: var(--space-2);
}
header {
  background-color: lightgray;
  padding-bottom: var(--space-2);
}
.hamburger {
  top: 0;
  right: 0;
  position: absolute;
}
h1 {
  padding: var(--space-1);
  margin: 0;
  font-size: 2em;
}
.desktop-sidebar {
  background-color: lightblue;
}
@media (min-width: 784px) {
  .hamburger {
    display: none;
  }
  main {
    display: flex;
    flex-direction: row;
  }
  aside {
    width: 300px;
  }
}
